library:
results = []
_failure = '<p style="color:red">%s: FAIL</p>'
_success = '<p style="color:green">%s: ok</p>'
_heading = '<h2>%s</h2>'

def heading(text):
    results.append(_heading%text)

def record(name, ok):
    if ok:
        results.append(_success%name)
    else:
        results.insert(0, _failure%name)

def show_results():
    print_html(''.join(results))
