function computer_select () {
    var selection = Math.floor(Math.random()*3);
    if (selection === 0) {
        return "rock";
    } else if (selection === 1) {
        return "paper";
    } else {
        return "scissors";
    }
}

function human_select() {
    var selection = prompt("Enter (r)ock, (p)aper or (s)cissors");
    if (selection === "r" || selection === "rock") {
        return "rock";
    } else if (selection === "p" || selection === "paper") {
        return "paper";
    } else if (selection === "s" || selection === "scissors") {
        return "scissors";
    } else {
        alert(selection + " is not a valid choice.");
        return selection;
    }
}

var player1, player2, match;



player1 = computer_select();
player2 = human_select();
match = player1 + " " + player2;

switch (match){
    case "rock rock":
    case "scissors scissors":
    case "paper paper":
        alert("Nobody wins");
        break;
    case "rock paper":
        alert("Player 2 wins");
        break;
    case "rock scissors":
        alert("Player 1 wins");
        break;
    case "paper rock":
        alert("Player 1 wins");
        break;
    case "paper scissors":
        alert("Player 2 wins");
        break;
    case "scissors rock":
        alert("Player 2 wins");
        break;
    case "scissors paper":
        alert("Player 1 wins");
        break;
    default:
        // would occur if we have an invalid choice
        // above; we already alerted the user in that
        // case so we just ignore it here.
}
