UsedRobot.prototype.teleport = function (x, y) {
    this.prev_x = this.x;
    this.prev_y = this.y;
    this.x = x;
    this.y = y;

    this._is_leaky = false;  // hide the leak
    RUR.world.add_frame();
    this._is_leaky = true;  // restore it
};

var Reeborg = new UsedRobot();
Reeborg.move();
Reeborg.teleport(3, 3);
Reeborg.turn_left();
Reeborg.teleport(4, 8);
Reeborg.move();
